import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useUser } from './Usercontext'; // Make sure this path is correct

const Portfolio = () => {
  const [PORTFOLIO, setPortfolio] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { userId } = useUser(); // Use the context to get the userId

  useEffect(() => {
    if (userId) {
      setLoading(true);
      axios.get(`/api/user/portfolio`, {
        headers: {
          // Assuming you are using sessions; if token-based auth, adjust accordingly
          'Authorization': `Bearer ${userId}`
        }
      })
        .then(response => {
          setPortfolio(response.data);
          setLoading(false);
        })
        .catch(error => {
          console.error('Error fetching portfolio:', error);
          setError('Failed to load portfolio. Please try again later.');
          setLoading(false);
        });
    } else {
      setError('No user ID found. Please log in.');
      setLoading(false);
    }
  }, [userId]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h1>Your Portfolio</h1>
      {portfolio.length > 0 ? (
        <ul>
          {portfolio.map((stock, index) => (
            <li key={index}>
              Symbol: {stock.symbol}, Name: {stock.name}, Quantity: {stock.quantity}, 
              Acquisition Price: {stock.acquisition_price}, Acquisition Date: {stock.acquisition_date}
            </li>
          ))}
        </ul>
      ) : (
        <p>Your portfolio is empty.</p>
      )}
    </div>
  );
};

export default Portfolio;
