import React from 'react';
import { Link } from 'react-router-dom';

function StockList({ stocks, onAddToPortfolio, isPortfolioView = false }) {
  if (!stocks || stocks.length === 0) {
    return <div>No stocks available.</div>;
  }

  return (
    <div>
      <h1>{isPortfolioView ? 'My Portfolio' : 'All Stocks'}</h1>
      <ul>
        {stocks.map((stock, index) => (
          <li key={stock.symbol || index}>
            <Link to={`/stock/${stock.symbol}`}>
              {stock.symbol} - {stock.name}
            </Link>
            {!isPortfolioView && onAddToPortfolio && (
              <button onClick={() => onAddToPortfolio(stock)} style={{marginLeft: '10px'}}>
                ADD TO PORTFOLIO
              </button>
            )}
            {isPortfolioView && (
              <Link to={`/stock/${stock.symbol}`} style={{marginLeft: '10px'}}>
                SEE DETAILS
              </Link>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default StockList;
