import React from 'react';
import { Link } from 'react-router-dom';


const isPortfolioView = false;

function onAddToPortfolio(stock) {
  console.log('Adding stock to portfolio:', stock);
}


function StockList({ stocks }) {
  if (!stocks || stocks.length === 0) {
    return <div>No stocks available.</div>;
  }

  return (
    <div>
      <h1>{isPortfolioView ? 'My Portfolio' : 'All Stocks'}</h1>
      <ul>
        {stocks.map(stock => (
          <li key={stock.symbol}>
            <span>{stock.symbol} - {stock.name}</span>
            {isPortfolioView ? (
              <Link to={`/stock/${stock.symbol}`}>SEE DETAILS</Link>
            ) : (
              <button onClick={() => onAddToPortfolio(stock)}>ADD TO PORTFOLIO</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}


export default StockList;
