import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function parseCSV(csvData) {
    const rows = csvData.trim().split('\n');
    const headers = rows.shift().split(',');

    return rows.map(row => {
        const values = row.split(',');
        return headers.reduce((object, header, index) => {
            object[header] = values[index];
            return object;
        }, {});
    });
}

function AllStocksPage() {
    const [stocks, setStocks] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        setIsLoading(true);
        axios.get('/api/all-stocks', { responseType: 'text' })
            .then(response => {
                const parsedData = parseCSV(response.data);
                setStocks(parsedData);
                setIsLoading(false);
            })
            .catch(error => {
                setError(error);
                setIsLoading(false);
            });
    }, []);

    if (error) {
        return <div>Error loading stocks: {error.message}</div>;
    }
    if (isLoading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>All Stocks</h1>
            <ul>
                {stocks.map((stock, index) => (
                    <li key={stock.symbol || index}>
                        <Link to={`/stock/${stock.symbol}`}>{stock.symbol} - {stock.name}</Link>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default AllStocksPage;
