import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import axios from 'axios';
import StockDetails from './Components/Stockdetails';
import StockList from './Components/Stocklist';
import Portfolio from './Components/Portfolio'; // Import the Portfolio component
import AddStockToPortfolio from './Components/AddstockToPortfolio'; // Import the AddStockToPortfolio component

function App() {
  const [stocks, setAllStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const userId = 1; // Mock user ID, replace with actual user ID retrieval logic

  const parseCSV = (csv) => {
    const lines = csv.split('\n');
    const result = [];
    const headers = lines[0].split(',');
    for (let i = 1; i < lines.length; i++) {
      const obj = {};
      const currentline = lines[i].split(',');
      for (let j = 0; j < headers.length; j++) {
        obj[headers[j]] = currentline[j];
      }
      result.push(obj);
    }
    return result;
  };

  useEffect(() => {
    axios.get('/api/all-stocks', { responseType: 'text' })
      .then(response => {
        try {
          const parsedData = parseCSV(response.data);
          setAllStocks(parsedData);
        } catch (parseError) {
          setError('Failed to parse stock data. Please try again later.');
        }
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load stocks. Please try again later.');
        setLoading(false);
      });
  }, []);

  const refreshPortfolio = () => {
    // Add logic here if you need to refresh the portfolio list
    // after adding a new stock, for example
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <Router>
      <Routes>
        <Route path="/" element={<StockList stocks={stocks} />} />
        <Route path="/stock/:symbol" element={<StockDetails />} />
        <Route path="/portfolio" element={<Portfolio userId={userId} />} /> {/* Portfolio route */}
        <Route path="/add-stock" element={<AddStockToPortfolio userId={userId} onStockAdded={refreshPortfolio} />} /> {/* AddStockToPortfolio route */}
      </Routes>
    </Router>
  );
}

export default App;
