import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import axios from 'axios';
import StockDetails from './Components/Stockdetails';
import StockList from './Components/Stocklist';
import Portfolio from './Components/Portfolio';
import { Link } from 'react-router-dom';


function App() {
  const [stocks, setAllStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [portfolioSymbols, setPortfolioSymbols] = useState([]);

  const parseCSV = (csv) => {
    const lines = csv.split('\n');
    const result = [];
    const headers = lines[0].split(',');
    for (let i = 1; i < lines.length; i++) {
      const obj = {};
      const currentline = lines[i].split(',');
      for (let j = 0; j < headers.length; j++) {
        obj[headers[j]] = currentline[j];
      }
      result.push(obj);
    }
    return result;
  };

  const handleAddToPortfolio = stock => {
    if (portfolioSymbols.some(item => item.symbol === stock.symbol)) {
      alert('Stock is already in your portfolio.');
    } else {
      setPortfolioSymbols([...portfolioSymbols, stock]);
    }
  };

  useEffect(() => {
    axios.get('/api/all-stocks', { responseType: 'text' })
      .then(response => {
        try {
          const parsedData = parseCSV(response.data);
          setAllStocks(parsedData);
        } catch (parseError) {
          setError('Failed to parse stock data. Please try again later.');
        }
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load stocks. Please try again later.');
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <Router>
      <Routes>
      <Route path="/" element={<StockList stocks={stocks} onAddToPortfolio={handleAddToPortfolio} isPortfolioView={false} />} />
        <Route path="/portfolio" element={<Portfolio stocks={portfolioSymbols} isPortfolioView={true} />} />
        <Route path="/stock/:symbol" element={<StockDetails />} />
      </Routes>
      <nav>
        <Link to="/">Home</Link> | <Link to="/portfolio">My Portfolio</Link>
      </nav>
    </Router>
  );
}

export default App;
